import React, { useState } from "react";
import axios from "axios";
import "./Excel.css";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [file, setFile] = useState(null);
  const [subOrderNo, setSubOrderNo] = useState("");
  const [filterResult, setFilterResult] = useState(null);

  const [data, setData] = useState({
    all: 0,
    rto: 0,
    door_step_exchanged: 0,
    delivered: 0,
    cancelled: 0,
    ready_to_ship: 0,
    shipped: 0,
    other: 0,
    totalSupplierListedPrice: 0,
    totalSupplierDiscountedPrice: 0,
    sellInMonth: 0,
    totalProfit: 0,
    deliveredSupplierDiscountedPriceTotal: 0,
    totalDoorStepExchanger: 0,
  });

  const [profitPercent, setProfitPercent] = useState(0);
  const [showFilteredView, setShowFilteredView] = useState(false);

  // 📊 Graph state
  const [graphData, setGraphData] = useState([]);
  const [showGraph, setShowGraph] = useState(false);

  // ✅ File validation
  const validateFile = (file) => {
    return (
      file &&
      (file.name.endsWith(".csv") ||
        file.name.endsWith(".xlsx") ||
        file.name.endsWith(".xls"))
    );
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (validateFile(selectedFile)) {
      setFile(selectedFile);
    } else {
      alert("Please upload a valid CSV or Excel file");
    }
  };

  // ✅ Filter handler
  const handleFilter = async () => {
    if (!subOrderNo) {
      alert("Please enter a Sub Order No.");
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/filter/${subOrderNo}`);
      setFilterResult(res.data);

      const calcProfit = 500 - res.data.discountedPrice;
      const calcProfitPercent = (calcProfit / 500) * 100;
      setProfitPercent(calcProfitPercent.toFixed(2));
      setShowFilteredView(true);
    } catch (err) {
      console.error("Filter failed", err);
      alert("No matching sub order found");
    }
  };

  // ✅ Upload + save handler
  const handleSubmitAll = async () => {
    if (!file) {
      alert("Please select a file first");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("file", file);

      const uploadRes = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const result = uploadRes.data;
      const totals = result.totals || {};

      const updatedData = {
        all: result.all?.length || 0,
        rto: result.rto?.length || 0,
        door_step_exchanged: result.door_step_exchanged?.length || 0,
        delivered: result.delivered?.length || 0,
        cancelled: result.cancelled?.length || 0,
        ready_to_ship: result.ready_to_ship?.length || 0,
        shipped: result.shipped?.length || 0,
        other: result.other?.length || 0,
        totalSupplierListedPrice: totals.totalSupplierListedPrice || 0,
        totalSupplierDiscountedPrice: totals.totalSupplierDiscountedPrice || 0,
        sellInMonth: totals.sellInMonthProducts || 0,
        totalProfit: totals.totalProfit || 0,
        deliveredSupplierDiscountedPriceTotal:
          totals.deliveredSupplierDiscountedPriceTotal || 0,
        totalDoorStepExchanger: totals.totalDoorStepExchanger || 0,
      };

      setData(updatedData);

      const calcProfitPercent =
        updatedData.sellInMonth > 0
          ? (updatedData.totalProfit / (updatedData.sellInMonth * 500)) * 100
          : 0;

      setProfitPercent(calcProfitPercent.toFixed(2));

      alert("✅ File processed and data saved to MongoDB!");
    } catch (err) {
      console.error("Submit all failed", err);
      alert("❌ Failed to process & store data");
    }
  };

  // 📊 Fetch profit graph data
  const handleShowGraph = async () => {
    try {
      const res = await axios.get("http://localhost:5000/profit-graph");
      setGraphData(res.data);
      setShowGraph(true);
    } catch (err) {
      console.error("Graph fetch failed", err);
      alert("❌ Failed to load profit graph");
    }
  };

  // ✅ PDF download
  const handleDownload = () => {
    fetch("http://localhost:5000/download", {
      method: "GET",
      headers: { Accept: "application/pdf" },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to download");
        return res.blob();
      })
      .then((blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "dashboard-report.pdf");
        document.body.appendChild(link);
        link.click();
        link.remove();
      })
      .catch((err) => {
        console.error("Download error:", err);
        alert("❌ Failed to download PDF. Please try again.");
      });
  };

  return (
    <div className="App">
      <nav className="navbar">
        <div className="navbar-logo">Meesho</div>
        <div className="navbar-search">
          <input
            type="search"
            placeholder="Add here Sub Order No"
            value={subOrderNo}
            onChange={(e) => setSubOrderNo(e.target.value)}
          />
          <button className="back-btn" onClick={handleFilter}>
            Filter
          </button>
          {showFilteredView && (
            <button
              className="back-btn"
              onClick={() => {
                setShowFilteredView(false);
                setProfitPercent(0);
              }}
            >
              Back
            </button>
          )}
        </div>
      </nav>

      <h1 className="heading">Product Status Dashboard</h1>

      {/* ✅ Dashboard vs Filtered View */}
      {!showFilteredView ? (
        <div className="status-boxes">
          <div className="box all">All<br /><span>{data.all}</span></div>
          <div className="box rto">RTO<br /><span>{data.rto}</span></div>
          <div className="box door_step_exchanged">
            Door Step Exchanged<br /><span>{data.door_step_exchanged}</span>
            <br />
            <small style={{ fontSize: "32px", color: "#222" }}>
              {data.totalDoorStepExchanger.toLocaleString()}
            </small>
          </div>
          <div className="box delivered">
            Delivered<br /><span>{data.delivered}</span>
            <br />
            <small style={{ fontSize: "32px", color: "#222" }}>
              ₹{data.deliveredSupplierDiscountedPriceTotal.toLocaleString()}
            </small>
          </div>
          <div className="box cancelled">Cancelled<br /><span>{data.cancelled}</span></div>
          <div className="box ready_to_ship">Pending<br /><span>{data.ready_to_ship}</span></div>
          <div className="box shipped">Shipped<br /><span>{data.shipped}</span></div>
          <div className="box other">Other<br /><span>{data.other}</span></div>
          <div className="box other">Supplier Listed Total Price<br /><span>{data.totalSupplierListedPrice.toLocaleString()}</span></div>
          <div className="box other">Supplier Discounted Total Price<br /><span>{data.totalSupplierDiscountedPrice.toLocaleString()}</span></div>
          <div className="box other">Total Profit<br /><span>{data.totalProfit.toLocaleString()}</span></div>
          <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
        </div>
      ) : (
        filterResult && (
          <div className="status-boxes">
            <div className="box other">Supplier Listed Price<br /><span>{filterResult.listedPrice.toLocaleString()}</span></div>
            <div className="box other">Supplier Discounted Price<br /><span>{filterResult.discountedPrice.toLocaleString()}</span></div>
            <div className="box other">Profit (per product)<br /><span>{(500 - filterResult.discountedPrice).toLocaleString()}</span></div>
            <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
          </div>
        )
      )}

      {/* ✅ File Upload */}
      <div className="upload-section">
        <p>Upload your CSV or Excel file</p>
        <input type="file" accept=".csv, .xlsx, .xls" onChange={handleFileChange} />
        {file && <p className="filename">Selected File: {file.name}</p>}
      </div>

      {/* ✅ Actions */}
      <div style={{ marginTop: "20px" }}>
        <button
          onClick={handleSubmitAll}
          disabled={!file}
          className="btn btn-success"
        >
          Submit All (Upload & Save)
        </button>
        <button onClick={handleDownload} className="btn btn-primary">
          Download PDF
        </button>
        <button onClick={handleShowGraph} className="btn btn-warning">
          Show Profit Graph
        </button>
      </div>

      {/* 📊 Profit Graph */}
      {showGraph && (
        <div style={{ marginTop: "40px", width: "100%", height: "400px" }}>
          <h2>📊 Date-wise Profit Graph</h2>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="profit" stroke="#ff5722" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}

export default App;
