import React, { useState } from 'react';
import axios from 'axios';
import './Excel.css';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('excel', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setData(res.data);
    } catch (err) {
      console.error("Upload failed", err);
    }
  };

  const renderSection = (title, items) => (
    <div>
      <h2>{title} ({items.length})</h2>
      <table>
        <thead>
          <tr>
            {items.length > 0 &&
              Object.keys(items[0]).map((key) => <th key={key}>{key}</th>)}
          </tr>
        </thead>
        <tbody>
          {items.map((row, index) => (
            <tr key={index}>
              {Object.values(row).map((val, i) => (
                <td key={i}>{val}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="App">
      <h1>Excel Category Filter</h1>

      <input type="file" accept=".xlsx, .xls" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      {data && (
        <div className="section-container">
          <div className="section-card">{renderSection("Delivered", data.delivered)}</div>
          <div className="section-card">{renderSection("RTO", data.rto)}</div>
          <div className="section-card">{renderSection("Return", data.return)}</div>
          <div className="section-card">{renderSection("Shipped", data.shipped)}</div>
          <div className="section-card">{renderSection("Buy Product", data.buyProduct)}</div>
          <div className="section-card">{renderSection("Unknown", data.unknown)}</div>
        </div>
      )}
    </div>
  );
}

export default App;
