import React, { useState } from "react";
import axios from "axios";
import "./Excel.css";

function App() {
  const [file, setFile] = useState(null);
  const [subOrderNo, setSubOrderNo] = useState("");
  const [filterResult, setFilterResult] = useState(null);

  const [data, setData] = useState({
    all: 0,
    delivered: 0,
    rto: 0,
    doorstep: 0,
    cancelled: 0,
    pending: 0,
    shipped: 0,
    other: 0,
    totalListed: 0,
    totalDiscounted: 0,
    totalProfit: 0,
    deliveredTotalDiscounted: 0,
    totalDoorstepPrice: 0,
    sellInMonth: 0,
  });

  const [profitPercent, setProfitPercent] = useState(0);
  const [dragActive, setDragActive] = useState(false);
  const [showFilteredView, setShowFilteredView] = useState(false);

  // ✅ File select
  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (
      selectedFile &&
      (selectedFile.name.endsWith(".csv") ||
        selectedFile.name.endsWith(".xlsx") ||
        selectedFile.name.endsWith(".xls"))
    ) {
      setFile(selectedFile);
    } else {
      alert("Please upload a valid CSV or Excel file");
    }
  };

  // ✅ Drag & Drop
  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const droppedFile = e.dataTransfer.files[0];
    if (
      droppedFile &&
      (droppedFile.name.endsWith(".csv") ||
        droppedFile.name.endsWith(".xlsx") ||
        droppedFile.name.endsWith(".xls"))
    ) {
      setFile(droppedFile);
    } else {
      alert("Only .csv or .xlsx files are supported");
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };
  const handleDragLeave = () => setDragActive(false);

  // ✅ Filter subOrderNo
  const handleFilter = async () => {
    if (!subOrderNo) {
      alert("Please enter a Sub Order No.");
      return;
    }

    try {
      const res = await axios.get(`http://localhost:5000/filter/${subOrderNo}`);
      setFilterResult(res.data);

      const calcProfit = 500 - res.data.discountedPrice;
      const calcProfitPercent = (calcProfit / 500) * 100;
      setProfitPercent(calcProfitPercent.toFixed(2));
      setShowFilteredView(true);
    } catch (err) {
      console.error("Filter failed", err);
      alert("No matching sub order found");
    }
  };

  // ✅ Upload file & fetch totals
  const handleSubmitAll = async () => {
    if (!file) {
      alert("Please select a file first");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("file", file);

      const uploadRes = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const result = uploadRes.data;
      const totals = result.totals || {};

      const updatedData = {
        all: result.all?.length || 0,
        delivered: result.delivered?.length || 0,
        rto: result.rto?.length || 0,
        doorstep: result.door_step_exchanged?.length || 0,
        cancelled: result.cancelled?.length || 0,
        pending: result.ready_to_ship?.length || 0,
        shipped: result.shipped?.length || 0,
        other: result.other?.length || 0,
        totalListed: totals.totalSupplierListedPrice || 0,
        totalDiscounted: totals.totalSupplierDiscountedPrice || 0,
        totalProfit: totals.totalProfit || 0,
        deliveredTotalDiscounted: totals.deliveredSupplierDiscountedPriceTotal || 0,
        totalDoorstepPrice: totals.totalDoorStepExchanger || 0,
        sellInMonth: totals.sellInMonthProducts || 0,
      };

      setData(updatedData);

      const calcProfitPercent =
        updatedData.sellInMonth > 0
          ? (updatedData.totalProfit / (updatedData.sellInMonth * 500)) * 100
          : 0;
      setProfitPercent(calcProfitPercent.toFixed(2));

      alert("File processed and data saved to MongoDB!");
    } catch (err) {
      console.error("Submit all failed", err);
      alert("Failed to process & store data");
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-logo">📦 Order Dashboard</div>
        <div className="navbar-search">
          <input
            type="search"
            placeholder="Enter Sub Order No"
            value={subOrderNo}
            onChange={(e) => setSubOrderNo(e.target.value)}
          />
          <button className="filter-btn" onClick={handleFilter}>
            Filter
          </button>
          {showFilteredView && (
            <button
              className="back-btn"
              onClick={() => setShowFilteredView(false)}
            >
              Back
            </button>
          )}
        </div>
      </nav>

      <h1 className="heading">Product Status Dashboard</h1>

      {/* Main dashboard */}
      {!showFilteredView ? (
        <div className="status-boxes">
          <div className="box all">All<br /><span>{data.all}</span></div>
          <div className="box delivered">
            Delivered<br />
            <span>{data.delivered}</span><br />
            <small>₹{data.deliveredTotalDiscounted.toLocaleString()}</small>
          </div>
          <div className="box rto">RTO<br /><span>{data.rto}</span></div>
          <div className="box doorstep">
            Doorstep Exchange<br />
            <span>{data.doorstep}</span><br />
            <small>₹{data.totalDoorstepPrice.toLocaleString()}</small>
          </div>
          <div className="box cancelled">Cancelled<br /><span>{data.cancelled}</span></div>
          <div className="box pending">Pending<br /><span>{data.pending}</span></div>
          <div className="box shipped">Shipped<br /><span>{data.shipped}</span></div>
          <div className="box other">Other<br /><span>{data.other}</span></div>

          <div className="box other">Supplier Listed Total<br /><span>₹{data.totalListed.toLocaleString()}</span></div>
          <div className="box other">Supplier Discounted Total<br /><span>₹{data.totalDiscounted.toLocaleString()}</span></div>
          <div className="box other">Total Profit<br /><span>₹{data.totalProfit.toLocaleString()}</span></div>
          <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
        </div>
      ) : (
        filterResult && (
          <div className="status-boxes">
            <div className="box other">Listed Price<br /><span>₹{filterResult.listedPrice.toLocaleString()}</span></div>
            <div className="box other">Discounted Price<br /><span>₹{filterResult.discountedPrice.toLocaleString()}</span></div>
            <div className="box other">Profit<br /><span>₹{(500 - filterResult.discountedPrice).toLocaleString()}</span></div>
            <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
          </div>
        )
      )}

      {/* File Upload */}
      <div
        className={`upload-section ${dragActive ? "drag-active" : ""}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <p>Drag & drop CSV/Excel file or click to upload</p>
        <input type="file" accept=".csv,.xlsx,.xls" onChange={handleFileChange} />
        {file && <p className="filename">Selected: {file.name}</p>}
      </div>

      <div style={{ marginTop: "20px" }}>
        <button className="submit-btn" onClick={handleSubmitAll}>
          Submit & Save
        </button>
      </div>
    </div>
  );
}

export default App;
