import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./Excel.css";

function App() {
  const [file, setFile] = useState(null);
  const [subOrderNo, setSubOrderNo] = useState("");
  const [filterResult, setFilterResult] = useState(null);
  const [graphData, setGraphData] = useState([]);
  const [showGraph, setShowGraph] = useState(false);
  const [dashboard, setDashboard] = useState(null);
  const [profitPercent, setProfitPercent] = useState(0);
  const [dragActive, setDragActive] = useState(false);
  const [showFilteredView, setShowFilteredView] = useState(false);

  // ✅ Fetch latest dashboard on load
  useEffect(() => {
    fetchDashboard();
  }, []);

  const fetchDashboard = async () => {
    try {
      const res = await axios.get("http://localhost:5000/dashboard");
      if (res.data && res.data.totals) {
        setDashboard(res.data);
        if (res.data.totals.totalSale > 0) {
          const percent =
            (res.data.totals.totalProfit / res.data.totals.totalSale) * 100;
          setProfitPercent(percent.toFixed(2));
        }
        setGraphData(res.data.profitByDate || []);
      }
    } catch (err) {
      console.error("Failed to fetch dashboard:", err);
    }
  };

  // ✅ Download PDF
  const handleDownload = () => {
    fetch("http://localhost:5000/download-pdf", {
      method: "GET",
      headers: { Accept: "application/pdf" },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to download");
        return res.blob();
      })
      .then((blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "dashboard-report.pdf");
        document.body.appendChild(link);
        link.click();
        link.remove();
      })
      .catch((err) => {
        console.error("Download error:", err);
        alert("Failed to download PDF. Please try again.");
      });
  };

  // ✅ File Validation
  const validateFile = (file) => {
    return (
      file &&
      (file.name.endsWith(".csv") ||
        file.name.endsWith(".xlsx") ||
        file.name.endsWith(".xls"))
    );
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (validateFile(selectedFile)) {
      setFile(selectedFile);
    } else {
      alert("Please upload a valid CSV or Excel file");
    }
  };

  // ✅ Drag & Drop
  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const droppedFile = e.dataTransfer.files[0];
    if (validateFile(droppedFile)) {
      setFile(droppedFile);
    } else {
      alert("Only .csv or .xlsx files are supported");
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };

  const handleDragLeave = () => {
    setDragActive(false);
  };

  // ✅ Upload & Process File
  const handleSubmitAll = async () => {
    if (!file) {
      alert("Please select a file first");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("file", file);

      await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      await fetchDashboard();
      alert("File processed and data saved to MongoDB!");
    } catch (err) {
      console.error("Submit all failed", err);
      alert("Failed to process & store data");
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-logo">Meesho</div>
        <div className="navbar-search">
          <input
            type="search"
            placeholder="Add here Sub Order No"
            value={subOrderNo}
            onChange={(e) => setSubOrderNo(e.target.value)}
          />
          <button className="back-btn" onClick={() => alert("Filter API not implemented yet")}>
            Filter
          </button>
        </div>
      </nav>

      <h1 className="heading">Product Status Dashboard</h1>

      {/* Main Dashboard */}
      {dashboard && (
        <div className="status-boxes">
          <div className="box delivered">
            Delivered<br />
            <span>{dashboard.categories.delivered.length}</span>
          </div>
          <div className="box rto">
            RTO<br />
            <span>{dashboard.categories.rto.length}</span>
          </div>
          <div className="box intransit">
            In Transit<br />
            <span>{dashboard.categories.inTransit.length}</span>
          </div>
          <div className="box other">
            Others<br />
            <span>{dashboard.categories.others.length}</span>
          </div>
          <div className="box other">
            Total Sale<br />
            <span>₹{dashboard.totals.totalSale.toLocaleString()}</span>
          </div>
          <div className="box other">
            Total Cost<br />
            <span>₹{dashboard.totals.totalCost.toLocaleString()}</span>
          </div>
          <div className="box other">
            Total Profit<br />
            <span>₹{dashboard.totals.totalProfit.toLocaleString()}</span>
          </div>
          <div className="box other">
            Profit %<br />
            <span>{profitPercent}%</span>
          </div>
        </div>
      )}

      {/* Profit Graph */}
      {dashboard && (
        <div style={{ margin: "20px 0" }}>
          <button
            onClick={() => setShowGraph(!showGraph)}
            style={{
              backgroundColor: "#17a2b8",
              color: "#fff",
              padding: "10px 20px",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              fontSize: "14px",
            }}
          >
            {showGraph ? "Hide Profit Graph" : "Show Profit Graph"}
          </button>
        </div>
      )}

      {showGraph && graphData.length > 0 && (
        <div style={{ margin: "40px 0", width: "100%", height: "400px" }}>
          <h2>Profit Trend (Per Date)</h2>
          <ResponsiveContainer>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Line
                type="monotone"
                dataKey="profit"
                stroke="#28a745"
                strokeWidth={3}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}

      {/* File Upload */}
      <div
        className={`upload-section ${dragActive ? "drag-active" : ""}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <p>Drag and drop your CSV or Excel file here</p>
        <input
          type="file"
          accept=".csv, .xlsx, .xls"
          onChange={handleFileChange}
        />
        {file && <p className="filename">Selected File: {file.name}</p>}
      </div>

      {/* Action Buttons */}
      <div style={{ marginTop: "20px" }}>
        <button
          onClick={handleSubmitAll}
          disabled={!file}
          style={{
            backgroundColor: "#28a745",
            color: "#fff",
            padding: "10px 20px",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            fontSize: "14px",
            marginRight: "10px",
          }}
        >
          Submit All (Upload & Save)
        </button>
        <button
          onClick={handleDownload}
          style={{
            backgroundColor: "#007bff",
            color: "#fff",
            padding: "10px 20px",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            fontSize: "14px",
          }}
        >
          Download PDF
        </button>
      </div>
    </div>
  );
}

export default App;
