import React from "react";
import { useLocation, Link } from "react-router-dom";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

function GraphPage() {
  const location = useLocation();
  const graphData = location.state?.graphData || [];

  return (
    <div style={{ padding: "20px" }}>
      <h2>Profit Trend (Per Date)</h2>
      {graphData.length > 0 ? (
        <div style={{ width: "100%", height: "400px" }}>
          <ResponsiveContainer>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="profit" stroke="#28a745" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      ) : (
        <p>No graph data available</p>
      )}
      <div style={{ marginTop: "20px" }}>
        <Link
          to="/"
          style={{
            backgroundColor: "#6c757d",
            color: "#fff",
            padding: "10px 20px",
            borderRadius: "6px",
            textDecoration: "none",
          }}
        >
          Back to Dashboard
        </Link>
      </div>
    </div>
  );
}

export default GraphPage;
