import React, { useState } from 'react';
import axios from 'axios';
import './Excel.css';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a file to upload.");

    const formData = new FormData();
    formData.append('excel', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setData(res.data);
    } catch (err) {
      console.error("Upload failed", err);
      alert("Failed to upload file. Please try again.");
    }
  };

  const getCount = (category) => (data?.[category]?.length || 0);

  return (
    <div className="App">
      <nav className="navbar">
        <div className="navbar-logo">Meesho</div>
        <div className="navbar-links">
          <a href="/">Home</a>
          <a href="/">Upload</a>
          <a href="/">Dashboard</a>
        </div>
      </nav>

      <h1 className="heading">Product Status Dashboard</h1>

      <div className="status-boxes">
        <div className="box delivered">
          Delivered<br /><span>{getCount("delivered")}</span>
        </div>
        <div className="box shipped">
          Shipped<br /><span>{getCount("shipped")}</span>
        </div>
        <div className="box buy">
          Buy<br /><span>{getCount("DOOR_STEP_EXCHANGED")}</span>
        </div>
        <div className="box rto">
          RTO<br /><span>{getCount("rto")}</span>
        </div>
        <div className="box return">
          Return<br /><span>{getCount("return")}</span>
        </div>
      </div>

      <div className="upload-section">
        <input type="file" accept=".xlsx, .xls" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload Excel File</button>
      </div>
    </div>
  );
}

export default App;
